import java.util.Scanner;
public class Ejercicio6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//6. Escribe un programa que pida por teclado una frase y cambie cada ocurrencia de la
		//palabra “si” por la palabra “no”. Después lo muestra por pantalla.
		
		Scanner teclado = new Scanner(System.in);
		
		String frase;
		String si = "si";
		String no = "no";
		
		System.out.println("introduzca una frase con sies que va a ser super gracioso, ya veras.....");
		
		frase=teclado.nextLine();
		
		if(frase.contains(si)){
			
			
		System.out.println(frase.replaceAll(si, no));
		}
		else{
			System.out.println("tienes que porner algun si");
		}
			
		
		
		
	}

}
